mapper.select:
		select `id`,`serviceName`,`serviceIP`,`servicePort`,`serviceParam`,`serviceResult` from service_table

			 and id = #{id} 
		  	 and serviceName = #{serviceName} 
		  	 and serviceIP = #{serviceIP} 
		  	 and servicePort = #{servicePort} 
		  	 and serviceParam = #{serviceParam} 
		  	 and serviceResult = #{serviceResult} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from service_tables

	 and id = #{id} 
	 and serviceName = #{serviceName} 
	 and serviceIP = #{serviceIP} 
	 and servicePort = #{servicePort} 
	 and serviceParam = #{serviceParam} 
	 and serviceResult = #{serviceResult}

mapper.update:
update service_table

	 id = #{id} 
	 serviceName = #{serviceName} 
	 serviceIP = #{serviceIP} 
	 servicePort = #{servicePort} 
	 serviceParam = #{serviceParam} 
	 serviceResult = #{serviceResult} 

where id = #{id}
